            //////////////////Selecting data corresponding to the order_id from orders table//////////////////////////
            $from="orders";
            $items=array(array("item_id","qty","delivery"));
            $where = array("order_id="=>$order_id);
            $omysql->Select($from,$where);
            $o_res = $omysql->arrayedResult;
            $o_rows=$omysql->affected;
            for($i=0;$i<$o_rows;$i++){
               $items[$i]['item_id']=$o_res[$i]["item_id"];
               $items[$i]['qty']=$o_res[$i]["qty"];
               $username=$o_res[$i]["user_nm"];
               $o_address=$o_res[$i]["address"];
               $date=$o_res[$i]["order_time"];
            }
