                            <?php

                            $entries = $o_rows; //number of items in placed order
                            $sno = 0;
                            $from = "items";
                            $grandtot = 0;
                            //fill table
                            while($sno < $entries)
                            {
                                echo "<tr>";
                                $id = $items[$sno]["item_id"]; //item_id of bought item
                                $quantity = $items[$sno]["qty"]; //qty bought
                                ///search for item details
                                $where = array("item_id like"=>$id);
                                $omysql->Select($from, $where);
                                $res = $omysql->arrayedResult;
                                $tot = $res[0]["cost"] + (($res[0]["cost"]*$res[0]["tax"])/100);
                                $grandtot+=$tot;
                                $sno++;
                                echo "<td>".$sno."</td>
                                <td>".$res[0]["item_nm"]."</td>
                                <td>₹".$res[0]["cost"]."</td>
                                <td>".$res[0]["tax"]."%</td>
                                <td>".$quantity."</td>
                                <td>₹".$tot."</td>";
                                echo "</tr>";

                            }
                            ?>
                            <tr><td></td><td></td><td></td>
                                <td><b>Grand Total</b>: </td><td></td>
                                <td><b>₹<?php echo $grandtot; ?></b></td>
